int n;
vector<int> V;
vector<bool> isprime;

void sieve(){
  int N = 10003;
  isprime.resize(N+1, true);
  isprime[0] = isprime[1] = false;

  for(int i = 2; i*i <= N; i++)
    if(isprime[i])
      for(int j = i*i; j <= N; j += i)
        isprime[j] = false;
  for(int i = 2; i <= N; i++)
    if(isprime[i])
      V.push_back(i);
}

bool prime(int x){
  if(x < 2) return false;
  for(auto d : V){
    if(d*d > x) break;
    if(x % d == 0)
      return false;
  }
  return true;
}

signed main(){
  fastio;
  cin >> n;
  if(n < 5){
    cout << 0;
    return 0;
  }
  sieve();
  auto p = lower_bound(P.begin(), P.end(), make_pair(n, 10000000));
  if(p != P.begin()) p--;
  auto [_n, sum] = *p;
  if(n < 100000) _n = 0, sum = 0;
  if(_n % 2 == 0) _n++;
  else _n += 2;

  while(_n <= n){
    if(prime(_n) && prime(_n-2))
      sum++;
    _n += 2;
  }

  cout << sum << "\n";
}
